mapper.select:
SELECT o.order_id as orderId, o.partner_id as partnerId, p.partner_name as partnerName, p.partner_type as partnerType, p.manager_name as managerName, p.contact_info as contactInfo, p.partner_address as partnerAddress, o.product_id as productId, pr.product_name as productName, pr.option_value as optionValue, pr.selling_price as sellingPrice, o.quantity, o.stock_status as stockStatus, o.created_at as createdAt FROM TB_ORDERS o JOIN TB_PARTNER p ON o.partner_id = p.partner_id JOIN TB_PRODUCT pr ON o.product_id = pr.product_id
AND o.order_id = #{orderId}
AND p.partner_name LIKE '%' || #{partnerName} || '%'
AND p.partner_type = #{partnerType}
AND o.created_at >= TO_DATE(#{startDate}, 'YYYY-MM-DD')
AND o.created_at <= TO_DATE(#{endDate}, 'YYYY-MM-DD')
AND pr.product_name LIKE '%' || #{productName} || '%'
AND pr.selling_price >= #{minPrice}
AND pr.selling_price <= #{maxPrice}
AND o.stock_status = #{stockStatus}
ORDER BY o.order_id DESC

mapper.update:
UPDATE TB_ORDERS
SET stock_status = #{stockStatus}
WHERE order_id = #{orderId}

mapper.select:
SELECT COUNT(*)
FROM TB_ORDERS o
JOIN TB_PARTNER p ON o.partner_id = p.partner_id
JOIN TB_PRODUCT pr ON o.product_id = pr.product_id

    
        AND o.order_id = #{orderId}
    
    
        AND p.partner_name LIKE '%' || #{partnerName} || '%'
    
    
        AND o.created_at >= TO_DATE(#{startDate}, 'YYYY-MM-DD')
    
    
        AND o.created_at <= TO_DATE(#{endDate}, 'YYYY-MM-DD')
    
    
        AND pr.product_name LIKE '%' || #{productName} || '%'
    
    
        AND pr.selling_price >= #{minPrice}
    
    
        AND pr.selling_price <= #{maxPrice}
    
    
        AND o.stock_status = #{stockStatus}
